import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../index.css";
import { storeLocal } from "../util/utils";

function Page1() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const endpoint_api = process.env.REACT_APP_endpoint_api;

  const [postObj, setPostObj] = useState({});
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    setLoading(true);
    axios
      .post(endpoint_api, postObj)
      .then((res) => {
        setData(res.data);
        try {
          navigate("/Registration");
          alert("Number received, proceed to register");
        } catch (e) {
          console.log(e);
          alert("Sorry,Could not process your data");
        }

        storeLocal("phone_number", postObj.number);
      })
      .catch((err) => {
        setError(err.message);
        console.log(err);
      })
      .finally(() => {
        setLoading(false);
      });
  };
  console.log(postObj);

  if (error) return <div>Error!</div>;
  if (loading) return <div>Loading... </div>;

  const handleHomeData = (event) => {
    const { name, value } = event.target;
    setPostObj((prevHomeData) => {
      return {
        ...prevHomeData,
        [name]: value,
      };
    });
  };
  return (
    <div>
      <div>
        <div className="page1" id="page1">
          <p>Hello World</p>
        </div>
      </div>
    </div>
  );
}
export default Page1;
