import React, { useState } from 'react';
import axios from 'axios';
import '../index.css';
import { storeLocal } from '../util/utils';

function Home() {
  const [data, setData] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [postObj, setPostObj] = useState({
    message: 'penzi',
    number: '',
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    setLoading(true);
    axios
      .post('http://localhost:5000/penzi', postObj)
      .then((res) => {
        setData(res.data);
        window.location.href = './Login';
        storeLocal('phone_number', postObj.number);
      })
      .catch((err) => {
        setError(err.message);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  if (error) return <div>Error!</div>;
  if (loading) return <div>Loading... </div>;

  const handleHomeData = (event) => {
    const { value, name } = event.target;
    setPostObj((prevHomeData) => {
      return {
        ...prevHomeData,
        [name]: value,
      };
    });
  };

  return (
    <div className='main'>
      <div>
        <div className='card'>
          <h2>Let's start registration</h2>
          <form className='form' onSubmit={handleSubmit}>
            <div>
              <label htmlFor='number'>Number</label>
              <input
                type='text'
                name='number'
                id='number'
                placeholder='07xx xxx xxx'
                required
                onChange={handleHomeData}
              />
            </div>
            <div className='button'>
              <button
                type='submit'
                className='btn'
                id='submit'
                // onSubmit={handleSubmit}
              >
                Submit
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
export default Home;
